<script setup>
import { ref } from 'vue'
import useEventsBus from "@/composables/eventBus";
import islandModal from "../modals/IslandModal.vue"
import GameDataService from "../../services/GameDataService"

const island = ref("")

const { emit } = useEventsBus();

const islands = ref([])

const getIslands = async () => {
  return GameDataService.getAllIslands()
    .then((res) => {
      islands.value = res.data.data;
    })
    .catch((error) => {
      console.log(error.response.data);
    });
};

getIslands();

function changeClickValue(hvalue){
   let i = 0;
   for(i; i < islands.value.length; i++){
      if(hvalue.target.attributes['title'].value === islands.value[i].name){
         emit('showIslandModal', { name: hvalue.target.attributes['title'].value });
      }
   }
   
}
</script>

